import React, { useState } from 'react';
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa';
import ReactPaginate from 'react-js-pagination';
import './MoreCompanies.css';

const MoreCompanies = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  const cardWidth = 220; // Width of each card including gap
  const cards = [
    { title: 'FMCG & Retail', link: '', count: '127 Companies', img: "data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAAAQABAAD/..." },
    { title: 'Startups', link: '#', count: '482 Companies' },
    { title: 'Edtech', link: '#', count: '152 Companies' },
    { title: 'Healthcare', link: '#', count: '571 Companies' },
    { title: 'Unicorns', link: '#', count: '99 Companies' },
    { title: 'Fintech', link: '#', count: '200 Companies' },
    { title: 'Biotech', link: '#', count: '150 Companies' },
    { title: 'AI & ML', link: '#', count: '300 Companies' },
    { title: 'Blockchain', link: '#', count: '120 Companies' },
    { title: 'Automotive', link: '#', count: '180 Companies' },
  ];
  const jobs = [
  { company: 'Revolut', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'it-services', natureOfBusiness: 'saas',img:"data:image/jpeg;base64," },
  { company: 'GoMechanic', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'internet', natureOfBusiness: 'b2b',img:"data:image/png;base64," },
  { company: 'Inshorts', title: 'Brand Designer', location: 'delhi', companyType: 'foreign-mnc', industry: 'engineering', natureOfBusiness: 'b2c',img:"data:image/jpeg;base64,"  },
  { company: 'Finastra', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'it-services', natureOfBusiness: 'saas' ,img:"data:image/jpeg;base64," },
  { company: 'Alight', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'internet', natureOfBusiness: 'b2b' ,img:"data:image/jpeg;base64," },
  { company: 'InfoStride', title: 'Brand Designer', location: 'delhi', companyType: 'foreign-mnc', industry: 'engineering', natureOfBusiness: 'b2c' ,img:"data:image/jpeg;base64," },
  { company: 'DataEconomy', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'it-services', natureOfBusiness: 'saas' ,img:"data:image/png;base64," },
  { company: 'G2', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'internet', natureOfBusiness: 'b2b',img:"data:image/png;base64,"  },
  { company: 'Goodera', title: 'Brand Designer', location: 'delhi', companyType: 'foreign-mnc', industry: 'engineering', natureOfBusiness: 'b2c' ,img:"data:image/png;base64," },
  { company: 'happay', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'it-services', natureOfBusiness: 'saas' ,img:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSCh7PPsUxGxIxOUyGsH_YP6SFlhrsGg8BNWdys605EzNufkQ6C7Kc-A2QsxqP0Jztrekw&usqp=CAU" },
  { company: 'Pagarbook', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'internet', natureOfBusiness: 'b2b',img:"data:image/png;base64,"  },
  { company: 'Dropbox', title: 'Brand Designer', location: 'delhi', companyType: 'foreign-mnc', industry: 'engineering', natureOfBusiness: 'b2c',img:"data:image/png;base64,"  },
  { company: 'Revolut', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'it-services', natureOfBusiness: 'saas',img:"data:image/png;base64,"  },
  { company: 'Hive', title: 'Brand Designer', location: 'delhi', companyType: 'foreign-mnc', industry: 'engineering', natureOfBusiness: 'b2c',img:"data:image/png;base64,"  },
  { company: 'Pando', title: 'Email Marketing', location: 'mumbai', companyType: 'startup', industry: 'it-services', natureOfBusiness: 'saas' ,img:"data:image/jpeg;base64," }, 
    // Add more jobs as needed...
  ];

  const maxScrollPosition = cardWidth * (cards.length - 1);

  const scrollRight = () => {
    if (scrollPosition < maxScrollPosition) {
      setScrollPosition(scrollPosition + cardWidth);
    }
  };

  const scrollLeft = () => {
    if (scrollPosition > 0) {
      setScrollPosition(scrollPosition - cardWidth);
    }
  };

  const [filters, setFilters] = useState({
    experience: 'mid',
    companyType: ['startup'],
    location: ['delhi'],
    industry: ['education'],
    natureOfBusiness: [],
    jobPosting: ''
  });

  const handleCheckboxChange = (name, value) => {
    setFilters(prev => {
      const currentValues = prev[name];
      const newValues = currentValues.includes(value)
        ? currentValues.filter(item => item !== value)
        : [...currentValues, value];
      return { ...prev, [name]: newValues };
    });
  };

  const handleRadioChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const filterJobs = (job) => {
    const { companyType, location, industry, natureOfBusiness } = job;
    return (
      (filters.companyType.length === 0 || filters.companyType.includes(companyType)) &&
      (filters.location.length === 0 || filters.location.includes(location)) &&
      (filters.industry.length === 0 || filters.industry.includes(industry)) &&
      (filters.natureOfBusiness.length === 0 || filters.natureOfBusiness.includes(natureOfBusiness)) &&
      (filters.jobPosting === '' || job.postingAge <= parseInt(filters.jobPosting.replace('<', '')))
    );
  };

 
  const [activePage, setActivePage] = useState(1);
  const itemsPerPage = 5; // Number of items per page
  const totalItemsCount = jobs.length;

  // Calculate the index of the first and last job card for the current page
  const indexOfLastItem = activePage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;

  const handlePageChange = (pageNumber) => {
    setActivePage(pageNumber);
  };

  return (
    <>
      <div className='view-all-company-main-carousel'>
        <div className="view-all-company-carousel">
          {scrollPosition > 0 && (
            <div className="view-all-company-arrow" onClick={scrollLeft}>
              <FaChevronLeft />
            </div>
          )}

          <div className="view-all-company-cards-container">
            <div
              className="view-all-company-cards"
              style={{ transform: `translateX(-${scrollPosition}px)` }}
            >
              {cards.map((card, index) => (
                <div key={index} className="view-all-company-card">
                  <h3>{card.title}</h3>
                  <a href={card.link}>{card.count} &gt;</a>
                </div>
              ))}
            </div>
          </div>

          {scrollPosition < maxScrollPosition && (
            <div className="view-all-company-arrow" onClick={scrollRight}>
              <FaChevronRight />
            </div>
          )}
        </div>
      </div>

      <div className="view-all-company-container">
        <div className="view-all-company-sidebar">
          <FilterGroup title="Experience level">
            {['entry', 'mid', 'expert'].map(level => (
              <label key={level}>
                <input
                  type="radio"
                  name="experience"
                  checked={filters.experience === level}
                  onChange={() => handleRadioChange('experience', level)}
                />
                {level.charAt(0).toUpperCase() + level.slice(1)} Level
              </label>
            ))}
          </FilterGroup>

          <FilterGroup title="Company type">
            {['corporate', 'foreign-mnc', 'indian-mnc', 'startup'].map(type => (
              <label key={type}>
                <input
                  type="checkbox"
                  checked={filters.companyType.includes(type)}
                  onChange={() => handleCheckboxChange('companyType', type)}
                />
                {type.replace('-', ' ')}
              </label>
            ))}
          </FilterGroup>

          <FilterGroup title="Location">
            {['pune', 'pimpri-chinchwad', 'mumbai', 'delhi', 'thane', 'new-mumbai'].map(loc => (
              <label key={loc}>
                <input
                  type="checkbox"
                  checked={filters.location.includes(loc)}
                  onChange={() => handleCheckboxChange('location', loc)}
                />
                {loc.replace('-', ' ')}
              </label>
            ))}
          </FilterGroup>

          <FilterGroup title="Industry">
            {['it-services', 'software-product', 'medical-services', 'education', 'internet', 'engineering'].map(ind => (
              <label key={ind}>
                <input
                  type="checkbox"
                  checked={filters.industry.includes(ind)}
                  onChange={() => handleCheckboxChange('industry', ind)}
                />
                {ind.replace('-', ' ')}
              </label>
            ))}
          </FilterGroup>

          <FilterGroup title="Nature of Business">
            {['b2b', 'b2c', 'saas', 'd2c', 'paas'].map(type => (
              <label key={type}>
                <input
                  type="checkbox"
                  checked={filters.natureOfBusiness.includes(type)}
                  onChange={() => handleCheckboxChange('natureOfBusiness', type)}
                />
                {type.toUpperCase()}
              </label>
            ))}
          </FilterGroup>

          <FilterGroup title="Job Posting">
            {['<7days', '<15days', '<30days'].map(posting => (
              <label key={posting}>
                <input
                  type="radio"
                  name="jobPosting"
                  checked={filters.jobPosting === posting}
                  onChange={() => handleRadioChange('jobPosting', posting)}
                />
                {posting.replace('<', 'Less than ')}
              </label>
            ))}
          </FilterGroup>
        </div>
        <JobListings jobs={jobs.filter(filterJobs)} />
      </div>
      <div className="view-all-company-pagination-container">
        <ReactPaginate
          activePage={activePage}
          itemsCountPerPage={itemsPerPage}
          totalItemsCount={totalItemsCount}
          pageRangeDisplayed={2}
          onChange={handlePageChange}
          itemClass="page-item"
          linkClass="page-link"
        />
      </div>
    </>
  );
};

const Sidebar = ({ filters, handleCheckboxChange, handleRadioChange }) => (
  <div className="view-all-company-sidebar">
    <FilterGroup title="Experience level">
      {['entry', 'mid', 'expert'].map(level => (
        <label key={level}>
          <input
            type="radio"
            name="experience"
            checked={filters.experience === level}
            onChange={() => handleRadioChange('experience', level)}
          />
          {level.charAt(0).toUpperCase() + level.slice(1)} Level
        </label>
      ))}
    </FilterGroup>

    <FilterGroup title="Company type">
      {['corporate', 'foreign-mnc', 'indian-mnc', 'startup'].map(type => (
        <label key={type}>
          <input
            type="checkbox"
            checked={filters.companyType.includes(type)}
            onChange={() => handleCheckboxChange('companyType', type)}
          />
 {type.replace('-', ' ')}
        </label>
      ))}
    </FilterGroup>

    <FilterGroup title="Location">
      {['pune', 'pimpri-chinchwad', 'mumbai', 'delhi', 'thane', 'new-mumbai'].map(loc => (
        <label key={loc}>
          <input
            type="checkbox"
            checked={filters.location.includes(loc)}
            onChange={() => handleCheckboxChange('location', loc)}
          />
          {loc.replace('-', ' ')}
        </label>
      ))}
    </FilterGroup>

    <FilterGroup title="Industry">
      {['it-services', 'software-product', 'medical-services', 'education', 'internet', 'engineering'].map(ind => (
        <label key={ind}>
          <input
            type="checkbox"
            checked={filters.industry.includes(ind)}
            onChange={() => handleCheckboxChange('industry', ind)}
          />
          {ind.replace('-', ' ')}
        </label>
      ))}
    </FilterGroup>

    <FilterGroup title="Nature of Business">
      {['b2b', 'b2c', 'saas', 'd2c', 'paas'].map(type => (
        <label key={type}>
          <input
            type="checkbox"
            checked={filters.natureOfBusiness.includes(type)}
            onChange={() => handleCheckboxChange('natureOfBusiness', type)}
          />
          {type.toUpperCase()}
        </label>
      ))}
    </FilterGroup>

    <FilterGroup title="Job Posting">
      {['<7days', '<15days', '<30days'].map(posting => (
        <label key={posting}>
          <input
            type="radio"
            name="jobPosting"
            checked={filters.jobPosting === posting}
            onChange={() => handleRadioChange('jobPosting', posting)}
          />
          {posting.replace('<', 'Less than ')}
        </label>
      ))}
    </FilterGroup>
  </div>
);

const FilterGroup = ({ title, children }) => (
  <div className="view-all-company-filter-group">
    <h3>{title}</h3>
    {children}
  </div>
);

const JobListings = ({ jobs }) => (
  <div className="view-all-company-job-listings">
    {jobs.map((job, index) => (
      <JobCard key={index} job={job} />
    ))}
  </div>
);

const JobCard = ({ job }) => (
  <div className="view-all-company-job-card" data-company-type={job.companyType} data-industry={job.industry} data-location={job.location}>
    <img className="view-all-company-company-logo" src={job.img} alt={`${job.company} logo`} />
    <div className="view-all-company-job-title">{`${job.company} - ${job.location}`}</div>
    <div className="view-all-company-job-location">{`${job.company} is looking for ${job.title}`}</div>
    <div className="view-all-company-job-tags">
      <div className="view-all-company-job-tag">{job.industry.toUpperCase()}</div>
    </div>
    <div className="view-all-company-hiring">HIRING</div>
  </div>
);

export default MoreCompanies;